/**
 * 受限制的可JSON化值的最小单位
 *
 * null 是不允许的
 *
 * undefined 在转换时会被删除
 */
declare type JSONValue = string | number | boolean | undefined;
/**
 * 对象JSON
 */
export declare type ObjectJson = {
    [str: string]: JSONValue | JSONable;
};
/**
 * 数组JSON
 */
export declare type ArrayJson = JSONValue[] | JSONable[];
/**
 * 可JSON化的Object
 */
export declare type JSONable = ObjectJson | ArrayJson;
export declare type Json<T extends JSONable = JSONable> = T;
/**
 * 扩展的可JSON化值
 *
 * 需要使用 ExpandedJSONable 转为字符串
 */
declare type ExJSONValue = JSONValue | URL | Date;
export declare type ExJSONable = {
    [str: string]: ExJSONValue | ExJSONable;
} | ExJSONValue[] | ExJSONable[];
export declare type Exjson<T extends ExJSONable = ExJSONable> = T;
export declare function ExJSONReplace(key: string, value: any): JSONValue | any[];
export {};
